import classes from './Filter.module.css'
import { FilterContext } from './FilterContext'
function Filter() {
    const { filterdMeals, setFilterMeals } = useContext(FilterContext)
    console.log(filterdMeals)

    const filteredList = (e)=>{
        setFilterMeals(e.target.value)
    }

  return (
    <nav className={classes.nav}>
        <div>
            <ul >
                <li onClick={filteredList} value="" id='Availble'> Availble</li>
                <li onClick={filteredList} value="bestSeller" id='bestSeller'> Beset Seller</li>
                <li onClick={filteredList} value="vegan" id='vegan'> Vegan</li>
                <li onClick={filteredList} value="deals" id='deals'> Deals</li>

            </ul>
        </div>
    </nav>
  )
}

export default Filter
