import React, { useContext } from "react";
import Modal from "../UI/Modal";
import classes from "./CartContent.module.css";
import { CartContext } from "../Cart/CartContext";
function CartContent(props) {
  const { quantity, setQuantity, cartItems } = useContext(CartContext);
  console.log(cartItems);

  return (
    <Modal hideCart={props.hideCart}>
      <div className={classes.overview}>
      <div className="">Meal
      {cartItems.map((item) => {
        return (
          <div className={classes.overview} type="scroll">
            <div className={classes.oneItem}>
              <div className={classes.name}> {item.name}</div>

            </div>
          </div>
        );
      })}
      </div>
      <div>Quantity
      {cartItems.map((item) => {
        return (
          <div className={classes.overview} type="scroll">
            <div className={classes.oneItem}>
              <div className={classes.quantity}>{quantity}x </div>

            </div>
          </div>
        );
      })}
      </div>
      <div>Price
      {cartItems.map((item) => {
        return (
          <div className={classes.overview} type="scroll">
            <div className={classes.oneItem}>
              <div className={classes.price}>
                {`$`}
                {item.price}
              </div>
            </div>
          </div>
        );
      })}
      </div>
      </div>

      <div onClick={props.hideCart}>
        <span className={classes.total}>Total Amount</span>
        <span>35.43</span>
      </div>
      <div className={classes.actions}>
        <button onClick={console.log("ordering")} className={classes.button}>
          order
        </button>
        <button onClick={props.hideCart} className={classes.button}>
          cancel
        </button>
      </div>
    </Modal>
  );
}

export default CartContent;
