                  //     const mealsInCart = prevMeals.map((meal) => meal);

                  //     if (mealsInCart.some((item) => item.id === meal.id)) {
                  //       return [...prevMeals];
                  //     } else {
                  //       return allMeals;
                  //     }
                  //   });
                  // }}
                >
                  Add To Cart
                </button>
                {/* <button onClick={decrease}>-</button> */}
              </div>
            </li>
          ))}
        </ul>
      </section>
    </Fragment>
  );
}

export default MealList;
