import { Fragment, useState } from "react";
import MealList from "./Components/Meals/MealList";

function App() {
  const DUMMY_MEALS = [
    {
      id: "m1",
      name: "Sushi",
      description: "Finest fish and veggies",
      price: 22.99,
    },
    {
      id: "m2",
      name: "Schnitzel",
      description: "A german specialty!",
      price: 16.5,
    },
    {
      id: "m3",
      name: "Barbecue Burger",
      description: "American, raw, meaty",
      price: 12.99,
    },
    {
      id: "m4",
      name: "Green Bowl",
      description: "Healthy...and green...",
      price: 18.99,
    }
  ];

  const increaseDecrease = (items) => {
    setItemsNumber(items);
  };
  return (
    <Fragment>
      <MealList onUpdate={increaseDecrease} meals={DUMMY_MEALS} />
    </Fragment>
  );
}

export default App;
